var analyticsEvents = [];

analyticsEvents.push = function() {

    // If the Require JS library has finished loading, we can pass the event to it
    if (typeof analyticsEvents.ready == 'function') {
        analyticsEvents.ready(arguments);
    }

    // Otherwise, push these arguments to the array ready for the JS to process it later
    else {
        // Nest the arguments as their own array
        arguments = [arguments];
        return Array.prototype.push.apply(this, arguments);
    }

};

